import React, { useState } from "react";
import yutkPopularT from "../yutkPopularT.jpg";
import yutkHoodie2 from "../yutkHoodie2.jpg";
import bags2 from "../bags2.jpg";
import bag from "../bag.jpg";
import bags from "../bags.jpg";
import "./Daypack.css";
import BlackBag1 from "../3ZipperBlackBagImages/BlackBag1.jpg";
import BlackBag2 from "../3ZipperBlackBagImages/BlackBag2.jpg";
import BlackBag3 from "../3ZipperBlackBagImages/BlackBag3.jpg";
import BlackBag4 from "../3ZipperBlackBagImages/BlackBag4.jpg";
import BlackBag5 from "../3ZipperBlackBagImages/BlackBag5.jpg";
import BlackBag6 from "../3ZipperBlackBagImages/BlackBag6.jpg";
import BlackBag7 from "../3ZipperBlackBagImages/BlackBag7.jpg";

function BlackBagThreeZipper() {
  const imageList = [
    { type: "image", src: BlackBag1 },
    { type: "image", src: BlackBag2 },
    { type: "image", src: BlackBag3 },
    { type: "image", src: BlackBag4 },
    { type: "image", src: BlackBag5 },
    { type: "image", src: BlackBag6 },
    { type: "image", src: BlackBag7 },
  ];

  const [selectedImage, setSelectedImage] = useState(imageList[0]);

  const handleImageSelect = (image) => {
    setSelectedImage(image);
  };

  return (
    <div className="main-display">
      <div className="left-display">
        <div className="left-pics">
          {imageList.map((image, index) => (
            <img
              key={index}
              src={image.src}
              alt={`Image ${index + 1}`}
              id="left-img"
              className={selectedImage === image ? "selected" : ""}
              onClick={() => handleImageSelect(image)}
            />
          ))}
        </div>
      </div>
      <div className="right-display">
          <img src={selectedImage.src} className="right-img" alt="Selected Image" />
      </div>
    </div>
  );
}

export default BlackBagThreeZipper;
