import React, { useState } from "react";
import yutkPopularT from "../yutkPopularT.jpg";
import yutkHoodie2 from "../yutkHoodie2.jpg";
import bags2 from "../bags2.jpg";
import bag from "../bag.jpg";
import bags from "../bags.jpg";
import "./Daypack.css";
import CasualBag1 from "../CasualBag1.JPG";
import CasualBag2 from "../CasualBag2.JPG";
import CasualBag3 from "../CasualBag3.JPG";
import CasualBag4 from "../CasualBag4.JPG";
import CasualBag5 from "../CasualBag5.JPG";
import CasualBag6 from "../CasualBag6.JPG";

function GymBag() {
  const imageList = [
    { type: "video", src: yutk_bag7 },
    { type: "image", src: CasualBag1 },
    { type: "image", src: CasualBag2 },
    { type: "image", src: CasualBag3 },
    { type: "image", src: CasualBag4 },
    { type: "image", src: CasualBag5 },
    { type: "image", src: CasualBag6 },
  ];

  const [selectedImage, setSelectedImage] = useState(imageList[0]);

  const handleImageSelect = (image) => {
    setSelectedImage(image);
  };

  return (
    <div className="main-display">
      <div className="left-display">
        <div className="left-pics">
          {imageList.map((image, index) => (
            <img
              key={index}
              src={image.src}
              alt={`Image ${index + 1}`}
              id="left-img"
              className={selectedImage === image ? "selected" : ""}
              onClick={() => handleImageSelect(image)}
            />
          ))}
        </div>
      </div>
      <div className="right-display">
        {selectedImage.type === "video" ? (
          <video controls loop autoPlay muted width="480" height="504">
            <source className="vid" src={yutk_bag} type="video/mp4" />
          </video>
        ) : (
          <img
            src={selectedImage.src}
            className="right-img"
            alt="Selected Image"
          />
        )}
      </div>
    </div>
  );
}

export default GymBag;
