import React, { useState } from "react";
import yutkPopularT from "../yutkPopularT.jpg";
import yutkHoodie2 from "../yutkHoodie2.jpg";
import bags2 from "../bags2.jpg";
import bag from "../bag.jpg";
import bags from "../bags.jpg";
import "./Backpack.css";
import yutk_bag from "../yutk_bag.mp4";

function Backpack() {
  const imageList = [
    { type: 'video', src: yutk_bag },
    { type: 'image', src: bag },
    { type: 'image', src: bags2 },
    { type: 'image', src: yutkHoodie2 },
  ];

  const [selectedImage, setSelectedImage] = useState(imageList[0]);

  const handleImageSelect = (image) => {
    setSelectedImage(image);
  };

  return (
    <div className="main-display">
      <div className="left-display">
        <div className="left-pics">
          {imageList.map((image, index) => (
            <img
              key={index}
              src={image.src}
              alt={`Image ${index + 1}`}
              id="left-img"
              className={selectedImage === image ? "selected" : ""}
              onClick={() => handleImageSelect(image)}
            />
          ))}
        </div>
      </div>
      <div className="right-display">
        {selectedImage.type === 'video' ? (
          <video controls loop autoPlay muted width="360" height="490">
            <source src={selectedImage.src} type="video/mp4" />
          </video>
        ) : (
          <img src={selectedImage.src} alt="Selected Image" />
        )}
      </div>
    </div>
  );
}

export default Backpack;
