import React, { useState } from "react";
import yutkPopularT from "../yutkPopularT.jpg";
import yutkHoodie2 from "../yutkHoodie2.jpg";
import bags2 from "../bags2.jpg";
import bag from "../bag.jpg";
import bags from "../bags.jpg";
import "./Daypack.css";
import FannyPack1 from "../FannyPack1.JPG";
import FannyPack2 from "../FannyPack2.JPG";
import FannyPack3 from "../FannyPack3.JPG";
import FannyPack4 from "../FannyPack4.JPG";
import FannyPack5 from "../FannyPack5.JPG";

function FannyPack() {
  const imageList = [
    { type: "image", src: FannyPack1 },
    { type: "image", src: FannyPack2 },
    { type: "image", src: FannyPack3 },
    { type: "image", src: FannyPack4 },
    { type: "image", src: FannyPack5 },
  ];

  const [selectedImage, setSelectedImage] = useState(imageList[0]);

  const handleImageSelect = (image) => {
    setSelectedImage(image);
  };

  return (
    <div className="main-display">
      <div className="left-display">
        <div className="left-pics">
          {imageList.map((image, index) => (
            <img
              key={index}
              src={image.src}
              alt={`Image ${index + 1}`}
              id="left-img"
              className={selectedImage === image ? "selected" : ""}
              onClick={() => handleImageSelect(image)}
            />
          ))}
        </div>
      </div>
      <div className="right-display">
        <img
          src={selectedImage.src}
          className="right-img"
          alt="Selected Image"
        />
      </div>
    </div>
  );
}

export default FannyPack;
